import React, { Component } from 'react';
import {Link} from "react-router-dom";
import axios from "axios";
import "./register.component.css";


const defaultForm = {
    name: '',
    email: '',
    phoneNumber: '',
    username: '',
    password: '',
    gender: '',
    DOB: '',
    address: ''
}
export  class RegisterComponent extends Component {
    constructor() {
        super();
        this.state = {
            data: {
                ...defaultForm,
            },
            error: {
                ...defaultForm,


            },
            isSubmitting: false,
            isValidForm: false,
            touched:false

        };
    }

    handleChange = (e) => {
        const { name, value } = e.target;
        this.setState((prevState) => ({
            data: {
                ...prevState.data,
                [name]: value
            }
        }), () => {
            this.validateForm(name);
        })
    }
    validateForm(fieldName) {
        let err;
        switch (fieldName) {
            case 'username':
                err = this.state.data[fieldName]
                    ? this.state.data[fieldName].length > 6
                        ? ''
                        : 'Username must be 6 characters'
                    : 'Username is required'
                break;
            case 'password':
                err = this.state.data[fieldName]
                    ? this.state.data[fieldName].length > 6
                        ? ''
                        : 'Weak Password'
                    : 'Password is required'
                break;
            case 'email':
                err = this.state.data[fieldName]
                    ? this.state.data[fieldName].includes('@')
                        ? ''
                        : 'Invalid Email'
                    : 'Email is required'
                break;
            default:
                break;
        }

        this.setState((pre) => ({
            error: {
                ...pre.error,
                [fieldName]: err
            }
        }), () => {
            const { error } = this.state;
            const errors = Object
                .values(error)
                .filter(err => err);
            console.log('error is >>', errors);

            let isValidForm = errors.length === 0;
            this.setState({
                isValidForm
            });
        })
    }

    handleSubmit = e => {
        e.preventDefault();
        this.setState({
            isSubmitting: true
        });
        // setTimeout(()=>{
        //     this.setState({
        //         isSubmitting:false
        //     });
        // },2000)
            axios.post
            ("http://localhost:2021/api/auth/register",        
              this.state.data,{
                headers:{
                    "Content-Type":"application/json",
                },
                params:{},
                responseType:"json"
            })
            .then(data=>{
                console.log("success in axios register>>",data);
                this.props.history.push("/")
            })
            .catch(err=>{
                console.log("error handle properly>>",err.response);
                this.setState((pre)=>{
                    return{
                        isSubmitting: false
                    }
                })
            })


    }
    handleTouched=()=>{
        this.setState({touched:true});
    }

    render() {

        let btn = this.state.isSubmitting
            ?
             <button className="wrapper"  onBlur={this.handleTouched} disabled={true}>submitting</button>
            :
             <button  className="wrapper"  disabled={!this.state.isValidForm} type="submit">Submit</button>
        return (
            <div className="registerbox">
                <h3>Register</h3>
                <form className="form-group" onSubmit={this.handleSubmit}>


                    <input type="text" placeholder="Name" name="name" className="form-control" onChange={this.handleChange}></input>

                    <input type="text" placeholder="Email" name="email" className="form-control" onChange={this.handleChange}></input>
                    <p className="danger">{this.state.error.email}</p>

                    <input type="text" placeholder="Address" name="address" className="form-control" onChange={this.handleChange}></input>

                    <input type="text" placeholder="Username" name="username" className="form-control" onChange={this.handleChange}></input>
                    <p className="danger">{this.state.error.username}</p>

                    <input type="password" placeholder="password" name="password" className="form-control" onChange={this.handleChange}></input>
                    <p className="danger">{this.state.error.password}</p>


                    <input type="radio" name="gender" value="male" onChange={this.handleChange}></input><span className="span">Male</span>

                    <input type="radio" name="gender" value="female" onChange={this.handleChange}></input> <span className="span">Female</span>


                    <input type="radio" name="gender" value="others" onChange={this.handleChange}></input> <span className="span">Others</span>




                    <br />

                    <input type="date" name="DOB" className="form-control" onChange={this.handleChange}></input>

                    {btn}
                </form>

                <p >Back to <Link to="/"><span className="span1">login</span></Link> </p>

            </div>
        )
    }
}
